package com.syntax.class01;

public class NonPrmitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Vidya";
		int age = 37;
		
		System.out.println("My name is " +name +" and my age is " + age);
	
		String fruit = "apple";
		double price = 1.99;
		System.out.println("one "+ fruit + " costs " + price );
		
		fruit = "mango";
		price = 3.99;
		System.out.println("one "+ fruit + " costs " + price );
	}

}
